mapper.select:
SELECT img_id, img_type, img_size
  FROM tbl_product_img
 WHERE img_id = #{fileId}

SELECT img_id, img_url, img_main
  FROM tbl_product_img
 WHERE product_id = #{productId}

mapper.update:
UPDATE tbl_product_img 
   SET img_main = 1 
 WHERE img_id = #{imgId}

UPDATE tbl_product_img 
   SET img_main = 0 
 WHERE img_id = #{imgId}
