mapper.select:
SELECT p.product_id, p.product_name, p.category_id, p.price, p.brand_id, p.seller_uid, p.size, p.quality, p.deal_means, p.safe_deal, p.update_date, p.selling_status, ct.category_name, i.img_url, cm.comment_id, cm.upper_id, cm.comment_time, cm.comment_contents FROM ( SELECT * FROM tbl_comment WHERE uid = #{uid}) cm, tbl_category ct, tbl_product_img i, tbl_product p WHERE cm.product_id = p.product_id AND p.category_id = ct.category_id AND p.product_id = i.product_id AND i.img_main = "1"
AND p.product_name LIKE CONCAT('%', #{searchValue}, '%')
ORDER BY p.update_date DESC LIMIT #{firstIndex}, #{rowsPerPage}

SELECT *
  FROM tbl_comment
 WHERE upper_id = #{inquireId}
   AND uid = #{sellerUid}
