mapper.select:
		SELECT COUNT(product_id) AS count
      	  FROM tbl_like
      	 WHERE uid = #{uid}

		SELECT COUNT(product_id) AS countLike
		  FROM tbl_like
       	 WHERE product_id = #{productId}

		SELECT COUNT(product_id) AS countLike
		  FROM tbl_like
       	 WHERE product_id = #{productId}
       	   AND uid = #{uid}

mapper.delete:
DELETE FROM tbl_like
 WHERE product_id = #{productId}
   AND uid = #{uid}
